example.code:
var grid = spreadsheet.ActiveGrid;
grid.QueryRange+=grid_QueryRange;
void grid_QueryRange(object sender, SpreadsheetQueryRangeEventArgs e)
{
   if (e.Cell.ColumnIndex == 5)
       e.ExcelStyle.ColorIndex = ExcelKnownColors.Blue;
}

